import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

/** FUTURE VERSION WILL HAVE:
 * Display the location for each move in the format (col, row) in the move history list.
Bold the currently selected item in the move list.
Rewrite Board to use two loops to make the squares instead of hardcoding them.
Add a toggle button that lets you sort the moves in either ascending or descending order.
When someone wins, highlight the three squares that caused the win.
When no one wins, display a message about the result being a draw.
 */

function Square(props) {

    return (
        <button className="square" onClick={props.onClick}>
            {props.value}
        </button>
    );
}




  class Board extends React.Component {

    renderSquare(i) {
      return (
            <Square 
                    value={this.props.squares[i]}
                    onClick={() => this.props.onClick(i)} 
      />);
      // passing a prop called value to square
    }

    render() {

      return (
        <div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
        </div>
      );
    }
  }

  class Game extends React.Component {

    constructor(props) {
        super(props);
        this.state = {

            history: [

            {
                moveRow: 0,
                moveColumn: 0,
                squares: Array(9).fill(null),
            }
        ],
            stepNumber: 0,
            xIsNext: true,
        };
    }

    jumpTo(step) {
        this.setState({
            stepNumber: step,
            xIsNext: (step % 2) === 0
        });
    }

    handleClick(i) {
        const history = this.state.history.slice(0, this.state.stepNumber + 1);
        const current = history[history.length - 1];
        const squares = current.squares.slice();
        if (calculateWinner(squares) || squares[i]) {
            return;
        }
        squares[i] = this.state.xIsNext ? 'X' : 'O';
        const row = parseInt(i / 3) + 1;
        const col = (i % 3) + 1;
        this.setState({
            history: history.concat([{
                moveRow: row,
                moveColumn: col,
                squares: squares,

            }]),
            stepNumber: history.length,
            xIsNext: !this.state.xIsNext,
        });
    }

    render() {
        const history = this.state.history;
        const current = history[this.state.stepNumber];
        const winner = calculateWinner(current.squares);
        // 0 if no one won, 1 is someone won, 2 is draw

        const moves = history.map((step, move) => {
            const desc = move ?
            `Go to move # ${move} at (row,col) = (${history[move].moveRow}, ${histor.moveColumn})` : 
            'Go to game start';
            return (
                <li key={move}>
                    <button className="move" onClick={() => this.jumpTo(move)}>{desc}</button>
                </li>
            );
        });

        let status;
        if (winner) {
            status = winner + ' has won!';
        } else {
            status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
        }
      return (
        <div className="game">
          <h1>Tic Tac Toe Supreme</h1>
          <div className="game-board">
            <Board 
                squares={current.squares}
                onClick={(i) => this.handleClick(i)}
            />
          </div>
          <div className="game-info">
            <div className="status">{status}</div>
            <ol className="moves">{moves}</ol>
          </div>
        </div>
      );
    }
  }

  function calculateWinner(squares) {
      const lines = [
          [0, 1, 2],
          [3, 4, 5],
          [6,7,8],
          [0,3,6],
          [1,4,7],
          [2,5,8],
          [0,4,8],
          [2,4,6],
      ];

      for (let i = 0; i < lines.length; i++) {
          const [a,b,c] = lines[i];
          if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
              return squares[a];
          }
      }

      return null;
  }


  // ========================================

  ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
